function findFactorialIterative(number) {
  let answer = 1;
  for (let i = 2; i &amp;lt;= number; i++) {
    answer = answer * i;
  }
  return answer;
}

function findFactorialRecursive(number) {
  if(number === 2) {
    return 2;
  }
  return number * findFactorialRecursive(number - 1)
}

findFactorialIterative(5); //O(n)
findFactorialRecursive(5); //O(n)


Fibonacci

My answer

function fibonacciIterative(n){
  var result = 0;
  var foreNum = 0;
  var beforeNum = 1;
  if(n===1){
    return 1;
  }
  for(var i = 0; i &amp;lt; n-1; i++){
    result = foreNum + beforeNum;
    if(i%2 === 0){
      foreNum = result;
    }else{
      beforeNum = result;
    }
  }
  return result;
}
fibonacciIterative(3);

function fibonacciRecursive(n) {
  var beforeNum;
  if(n === 0 || n === 1){
    return n;
  }
  beforeNum = fibonacciRecursive(n-2);

  return beforeNum + fibonacciRecursive(n-1);
}

fibonacciRecursive(8)


